		} else {
			dates.current.classList.add('hidden')
			dateRange.current.classList.remove('hidden')
		}
	}

	React.useEffect(() => {
		stepSlider.current.firstElementChild.classList.add('activeStep')
		// dates.current.classList.add('hideDates')
	}, []);

